CREATE TABLE Donor (
    D_ID INT PRIMARY KEY,
    D_Name VARCHAR(100) NOT NULL,
    D_Email VARCHAR(100) UNIQUE,
    D_Gender CHAR(1),
    D_DOB DATE,
    D_Age INT,
    D_Address VARCHAR(200),
    D_Contact VARCHAR(100),
    D_Reg_Date DATE,
    Emp_ID INT,
    FOREIGN KEY (Emp_ID) REFERENCES RecordingStaff(Emp_ID)
);


