CREATE TABLE Patient (
    P_ID INT PRIMARY KEY,
    P_Name VARCHAR(100),
    P_Email VARCHAR(100) UNIQUE,
    P_Contact VARCHAR(100),
    P_Gender CHAR(1),
    P_DOB DATE,
    P_Age INT,
    P_Bld_Grp VARCHAR(5),
    P_Address VARCHAR(200),
    Req_Quant INT,
    H_ID INT,
    FOREIGN KEY (H_ID) REFERENCES Hospital(H_ID)
);


